import { useContext, useState } from "react";
import { Context } from "../../utils/context";
import RelatedProducts from "./RelatedProducts/RelatedProducts";
import {
    FaFacebookF,
    FaTwitter,
    FaInstagram,
    FaLinkedinIn,
    FaPinterest,
    FaCartPlus,
} from "react-icons/fa";
import "./SingleProduct.scss";
import prod from "../../assets/products/earbuds-prod-1.webp"

import  useFetch from "../../hooks/useFetch";
import {useParams} from "react-router-dom";

const SingleProduct = () => {

    //useParams() return a object but we have destructured it and got a id value, basically it returns {id: 'value'} like this if we dont destructured it
    const {id} = useParams();  //why we writing id to get value from useParam(), becoz we made it a key in app.js, where we wrote id in Route 'products/:id' so id is basically a key
    const {data} = useFetch(`/api/products?populate=*&[filters][id] = ${id}`) //filter on the basis of id

    console.log(data);


    // if(!data) return;
    // const product = data?.data?.[0]?.attributes

    return (
        <div className="single-product-main-content">
            <div className="layout">
                <div className="single-product-page">

                    <div className="left">
                        <img src=
                            {prod} alt=""/>
                    </div>

                    <div className="right">
                        <span className="name">{""}</span>
                        <span className="price">{""}</span>
                        <span className="desc">{""}</span>

                        <div className="cart-buttons">
                            <div className="quantity-buttons">
                                <span>-</span>
                                <span>5</span>
                                <span>+</span>
                            </div>
                            <button className="add-to-cart-button">
                                <FaCartPlus size={20}/>
                                ADD TO CART
                            </button>
                        </div>

                        <span className="divider"/>

                        <div className="info-item">
                            <span className="text-bold">
                                Category:</span>
                                <span>Headphones</span>
                            <span className="text-bold">
                                Share:
                                <span className="social-icons">
                                    <FaFacebookF size={16}/>
                                    <FaTwitter size={16}/>
                                    <FaInstagram size={16}/>
                                    <FaLinkedinIn size={16}/>
                                    <FaPinterest size={16}/>
                                </span> 
                            </span>
                        </div>

                    </div>
                </div>
                <RelatedProducts/>
            </div>
        </div>
    );
};

export default SingleProduct;
